mapper.select:
/* PortfolioMapper.selectPortfolioList */ SELECT portfolio_no , portfolio_name , url , view_name , work_date , thumbnail , display_yn FROM portfolio
display_yn = #{domain.displayYN}
ORDER BY work_date DESC
LIMIT #{skipRows}, #{pageSize}

mapper.insert:
/* PortfolioMapper.insertPortfoilo */
INSERT portfolio
SET 
	  portfolio_no = #{portfolioNo}
	, portfolio_name = #{portfolioName}
	, url = #{url}
	, view_name = #{viewName}
	, work_date = #{workDate}
	, thumbnail = #{thumbnail}
	, display_yn = #{displayYN}

mapper.delete:
/* PortfolioMapper.deletePortfolio */
DELETE FROM portfolio
WHERE portfolio_no = #{portfolioNo}
