mapper.select:
/* PortfolioMapper.selectPortfolioList */ SELECT portfolio_no , portfolio_name , url , view_name , work_date , thumbnail , display_yn FROM portfolio
display_yn = #{domain.displayYN}
ORDER BY work_date DESC
LIMIT #{skipRows}, #{pageSize}

mapper.insert:
/* PortfolioMapper.insertPortfoilo */
INSERT portfolio
SET 
	  portfolio_no = #{portfolioNo}
	, portfolio_name = #{portfolioName}
	, start_date = CAST(#{startDate} AS DATETIME)
	, end_date = CAST(#{endDate} AS DATETIME)
	, thumbnail = #{thumbnail}
	, url = #{url}
	, main_image = #{mainImageName}
	, title = #{title}
	, description = #{description}
	, info = #{info}
	, participant = #{participant}
	, sub_image1 = #{subImageName1}
	, sub_image2 = #{subImageName2}
	, sub_image3 = #{subImageName3}
	, view_name = #{viewName}
	, work_date = NOW()
	, display_yn = #{displayYN}

mapper.update:
/* PortfolioMapper.updatePortfolio */
UPDATE portfolio
SET
	  display_yn = #{displayYN}

	, start_date = CAST(#{startDate} AS DATETIME)


	, end_date = CAST(#{endDate} AS DATETIME)


	, thumbnail = #{thumbnail}


	, main_image = #{mainImageName}


	, title = #{title}


	, description = #{description}


	, info = #{info}


	, participant = #{participant}


	, sub_image1 = #{subImageName1}


	, sub_image2 = #{subImageName2}


	, sub_image3 = #{subImageName3}

WHERE
	portfolio_no = #{portfolioNo}

mapper.delete:
/* PortfolioMapper.deletePortfolio */
DELETE FROM portfolio
WHERE portfolio_no = #{portfolioNo}
