mapper.select:
/* RecentlyMapper.selectRecentlyList */ SELECT recently_no , recently_name , participation_part , create_date , url , display_yn FROM recently
display_yn = #{domain.displayYN}
ORDER BY create_date DESC
LIMIT #{skipRows}, #{pageSize}

mapper.insert:
/* RecentlyMapper.insertRecently */
INSERT recently
SET 
	  recently_no = #{recentlyNo}
	, recently_name = #{recentlyName}
	, participation_part = #{participationPart}
	, create_date = NOW()
	, url = #{url}
	, start_date = #{startDate}
	, end_date = #{endDate}
	, display_yn = #{displayYN}

mapper.delete:
/* RecentlyMapper.deleteRecently */
DELETE FROM recently
WHERE recently_no = #{recentlyNo}
