mapper.select:
/* RecentlyMapper.selectRecentlyListCount */ SELECT COUNT(1) FROM recently 1 = 1
AND display_yn = #{displayYN}
AND recently_name LIKE CONCAT('%', #{recentlyName}, '%')
AND start_date > CAST(#{startDate} AS DATETIME)
AND end_date < CAST(#{endDate} AS DATETIME)
AND company_name LIKE CONCAT('%', #{companyName}, '%')
AND participation_part = #{participationPart}

/* RecentlyMapper.selectRecentlyList */ SELECT recently_no , recently_name , company_name , participation_part , create_date , url , display_yn , sort FROM recently 1 = 1
AND display_yn = #{displayYN}
AND recently_name LIKE CONCAT('%', #{recentlyName}, '%')
AND start_date > CAST(#{startDate} AS DATETIME)
AND end_date < CAST(#{endDate} AS DATETIME)
AND company_name LIKE CONCAT('%', #{companyName}, '%')
AND participation_part = #{participationPart}
ORDER BY sort DESC
LIMIT #{skipRows}, #{pageSize}

mapper.insert:
/* RecentlyMapper.insertRecently */
INSERT recently
SET 
	  recently_no = #{recentlyNo}
	, recently_name = #{recentlyName}
	, company_name = #{companyName}
	, participation_part = #{participationPart}
	, create_date = NOW()
	, url = #{url}
	, start_date = CAST(#{startDate} AS DATETIME)
	, end_date = CAST(#{endDate} AS DATETIME)
	, display_yn = #{displayYN}
	, sort = #{sort}

mapper.update:
/* RecentlyMapper.updateRecently */
UPDATE recently
SET
	  recently_name = #{recentlyName}
	, url = #{url}
	, display_yn = #{displayYN}

	, company_name = #{companyName}


	, participation_part = #{participationPart}


	, start_date = CAST(#{startDate} AS DATETIME)


	, end_date = CAST(#{endDate} AS DATETIME)

WHERE
	recently_no = #{recentlyNo}

mapper.delete:
/* RecentlyMapper.deleteRecently */
DELETE FROM recently
WHERE recently_no = #{recentlyNo}

mapper.update:
/* RecentlyMapper.updateRecentlySort */
UPDATE recently
SET
	sort = #{newSort}
WHERE
	sort = #{oldSort}
